const start = document.getElementById('start');
const reset = document.getElementById('reset');
const time = document.getElementById('time');
const times = document.getElementById('times')
const hours = document.getElementById('hours');
const minutes = document.getElementById('minutes');
const seconds = document.getElementById('seconds');
var countTime = 0;
var isStart = false; 

var i = 0;
var txt = 'Chronomètre en Javascript.';
var speed = 100;
typeWriter();
function typeWriter() {
  if (i < txt.length) {
    document.getElementById("title").innerHTML += txt.charAt(i);
    i++;
    //Pour donner un effet naturel
    let speedBis = Math.random() * (speed+50 - speed) + speed;
    setTimeout(typeWriter, speedBis);
  }
}

start.addEventListener('click',startAction);
reset.addEventListener('click',resetAction);
time.addEventListener('click',timeAction);

function timeAction(){  
    beep();

    //Count ++
    countTime++;

    //Création des différents éléments
    let div = document.createElement('div');
    let element = document.createElement('p');
    let button = document.createElement('button');

    //Création du button
    button.innerHTML = 'X';
    button.setAttribute('class','btn-delete')
    button.addEventListener('click',deleteTime);

    //Création du p 
    element.innerHTML = 'Temps n°'+countTime+' : <strong>'+hours.innerHTML +':'+minutes.innerHTML+':'+seconds.innerHTML+'</strong>';
    
    div.appendChild(button);
    div.appendChild(element);
    div.setAttribute('class','time')
    times.appendChild(div);

}

function deleteTime(){
    beep();
    //Recherche du p ayant le mettre data attribute     
    let elements = document.getElementsByTagName('p');
    times.removeChild(this.parentNode);
}
function resetAction(){
    beep();

    isStart = false;
    seconds.innerHTML = "00";
    minutes.innerHTML = "00";
    hours.innerHTML = "00";
    times.innerHTML = "";
    start.setAttribute('style','background-color: #2da800;');
    countTime = 0;
}

function startAction(){
    beep();

    if(!isStart){
        isStart = true;
        start.innerHTML = "Stop";
        start.setAttribute('style','background-color: #d20101;');
        startChrono();
    }else{
        start.setAttribute('style','background-color: #2da800;');
        isStart = false
        start.innerHTML = "Start";
    }
}

async function startChrono(){
    while(isStart){
        await sleep(1000);
        if(isStart){
            seconds.innerHTML++;
            if(seconds.innerHTML == 60){
                seconds.innerHTML=00;
                minutes.innerHTML++;
                if(minutes.innerHTML == 60){
                    hours.innerHTML++;
                    minutes.innerHTML = 00;
                }
            }
        designChrono();

        }

    }
}


function designChrono(){
    if(minutes.innerHTML<10 && minutes.innerHTML.length < 2){
        minutes.innerHTML = "0" + minutes.innerHTML;
    }
    if(seconds.innerHTML<10 && seconds.innerHTML.length < 2){
        seconds.innerHTML = "0" + seconds.innerHTML;
    }
    if(hours.innerHTML<10 && hours.innerHTML.length < 2){
        hours.innerHTML = "0" + hours.innerHTML;
    }
}
function sleep(delay) {
    return new Promise((resolve) => setTimeout(resolve, delay))
  }

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}